<script>

 document.getElementById('select_all_ids').addEventListener('change', function () {
        // Récupérer toutes les cases à cocher
        var checkboxes = document.querySelectorAll('.checkbox_ids');

        // Si la case "Sélectionner tout" est cochée, toutes les cases sont cochées
        checkboxes.forEach(function(checkbox) {
            checkbox.checked = document.getElementById('select_all_ids').checked;
        });
    });
</script>
